import requests
from urllib3.exceptions import MaxRetryError

# 尝试连接 3 次，间隔 1 秒
retries = 3
delay = 1

def send_request(url, data, headers):
    try:
        response = requests.post(url=url, data=json.dumps(data), headers=headers, timeout=10, verify=False)
        response.raise_for_status()
        return response.json()
    except requests.exceptions.ConnectionError as e:
        print(f"ConnectionError: {e}")
        raise
    except requests.exceptions.Timeout as e:
        print(f"Timeout: {e}")
        raise
    except requests.exceptions.RequestException as e:
        print(f"RequestException: {e}")
        raise

def retry_send_request(url, data, headers):
    for attempt in range(retries):
        try:
            return send_request(url, data, headers)
        except MaxRetryError as e:
            if attempt < retries - 1:
                time.sleep(delay)
                continue
        raise MaxRetryError("Max retries exceeded")

# 使用 retry_send_request 函数发送请求
